INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(99993, 'Abstracts in anthropology (en)', 'Abstracts in anthropology (sv)', 'Abstracts in Anthropology covers a broad spectrum of current anthropological topics from a vast number of periodicals. Several thousand abstracts, selected and classified, fully indexed by author and subject, provide a thorough coverage of anthropological scholarship in all its subfields: Cultural Anthropology, Physical Anthropology, Archaeology, Linguistics. (en)', 'Abstracts in Anthropology covers a broad spectrum of current anthropological topics from a vast number of periodicals. Several thousand abstracts, selected and classified, fully indexed by author and subject, provide a thorough coverage of anthropological scholarship in all its subfields: Cultural Anthropology, Physical Anthropology, Archaeology, Linguistics. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:54:04', '2021-08-05T13:55:24');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(99994, 'Acland''s Video Atlas of Human Anatomy (en)', 'Acland''s Video Atlas of Human Anatomy (sv)', 'Online educational videos designed to help students of the health sciences as well as practitioners to gain an anatomical understanding of highly difficult-to-dissect parts of the human body. (en)', 'Online educational videos designed to help students of the health sciences as well as practitioners to gain an anatomical understanding of highly difficult-to-dissect parts of the human body. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:54:04', '2022-09-16T15:12:46');
