INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(99996, 'Acta Sanctorum (en)', 'Acta Sanctorum (sv)', 'The Acta Sanctorum Database is an electronic version of the complete printed text of Acta Sanctorum, from the edition published in sixty-eight volumes by the Société des Bollandistes in Antwerp and Brussels. It is a collection of documents examining the lives of Christian saints, organised according to each saint''s feast day, and runs from the two January volumes published in 1643 to the Propylaeum to December published in 1940.  The Acta Sanctorum Database contains the complete Acta Sanctorum, including all prefatory material, original texts, critical apparatus and indices. Bibliotheca Hagiographica Latina reference numbers, essential references for scholars, are also included.    Acta Sanctorum Database and Patrologia Latina Database are cross-searchable. (en)', 'The Acta Sanctorum Database is an electronic version of the complete printed text of Acta Sanctorum, from the edition published in sixty-eight volumes by the Société des Bollandistes in Antwerp and Brussels. It is a collection of documents examining the lives of Christian saints, organised according to each saint''s feast day, and runs from the two January volumes published in 1643 to the Propylaeum to December published in 1940.  The Acta Sanctorum Database contains the complete Acta Sanctorum, including all prefatory material, original texts, critical apparatus and indices. Bibliotheca Hagiographica Latina reference numbers, essential references for scholars, are also included.    Acta Sanctorum Database and Patrologia Latina Database are cross-searchable. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:54:04', '2022-08-19T11:59:47');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(99997, 'Africa Yearbook Online (en)', 'Africa Yearbook Online (sv)', 'The Africa Yearbook covers major domestic political developments, the foreign policy and socio-economic trends in sub-Sahara Africa – all related to developments in one calendar year. The Africa Yearbook contains articles on all sub-Saharan states, each of the four sub-regions (West, Central, Eastern, Southern Africa) focusing on major cross-border developments and sub-regional organizations as well as one article on continental developments and one on European-African relations. (en)', 'The Africa Yearbook covers major domestic political developments, the foreign policy and socio-economic trends in sub-Sahara Africa – all related to developments in one calendar year. The Africa Yearbook contains articles on all sub-Saharan states, each of the four sub-regions (West, Central, Eastern, Southern Africa) focusing on major cross-border developments and sub-regional organizations as well as one article on continental developments and one on European-African relations. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:54:04', '2021-08-05T14:04:15');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(99998, 'African American Music Reference (en)', 'African American Music Reference (sv)', 'African American Music Reference offers a comprehensive coverage of blues, jazz, spirituals, civil rights songs, slave songs, minstrelsy, rhythm and blues, gospel, and other forms of black American musical expression. (en)', 'African American Music Reference offers a comprehensive coverage of blues, jazz, spirituals, civil rights songs, slave songs, minstrelsy, rhythm and blues, gospel, and other forms of black American musical expression. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:54:04', '2021-10-01T11:28:31');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(99999, 'African Studies Companion Online (en)', 'African Studies Companion Online (sv)', 'The African Studies Companion Online is a tool for scholars in African Studies. It contains guides and resources for African languages, Africa cartography and maps, African film, African studies journals, magazines and newsletters, media guides and news sources for Africa, the African press, African studies library collections worldwide, national archives in Africa, centers of African studies and African studies programs worldwide, awards and prizes in African studies, and a wealth of other subjects. Many entries describe and evaluate resources, others are factual and provide practical information.
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100000, 'Agricola (en)', 'Agricola (sv)', 'AGRICOLA (AGRICultural OnLine Access) serves as the catalogue and index to the collections of the National Agricultural Library, as well as a primary public source for world-wide access to agricultural information. The database covers materials in all formats and periods, including printed works from as far back as the 15th century. (en)', 'AGRICOLA (AGRICultural OnLine Access) serves as the catalogue and index to the collections of the National Agricultural Library, as well as a primary public source for world-wide access to agricultural information. The database covers materials in all formats and periods, including printed works from as far back as the 15th century. (sv)', FALSE, '', '', FALSE, TRUE, 'freely_available', FALSE, '2016-02-19T10:54:04', '2019-06-17T11:46:43');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100001, 'Alberti Magni Opera Omnia (en)', 'Alberti Magni Opera Omnia (sv)', 'The digital edition of the Opera Omnia Alberti Magni was established on the basis of the critical edition published by Albertus-Magnus-Institute, which is funded by Arch-Diocese of Cologne, with Aschendorff Verlag. The Editio Coloniensis, named after the institutional seat of the institute, is the first complete edition of the works of Saint Albert the Great to fulfil the standards of modern editorial policies. (en)', 'The digital edition of the Opera Omnia Alberti Magni was established on the basis of the critical edition published by Albertus-Magnus-Institute, which is funded by Arch-Diocese of Cologne, with Aschendorff Verlag. The Editio Coloniensis, named after the institutional seat of the institute, is the first complete edition of the works of Saint Albert the Great to fulfil the standards of modern editorial policies. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:54:04', '2021-08-09T16:04:36');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100002, 'Algaebase (en)', 'Algaebase (sv)', 'AlgaeBASE is a database of information on algae that includes terrestrial, marine and freshwater organisms. At present, the data for the marine algae, particularly seaweeds, are the most complete. (en)', 'AlgaeBASE is a database of information on algae that includes terrestrial, marine and freshwater organisms. At present, the data for the marine algae, particularly seaweeds, are the most complete. (sv)', FALSE, '', '', FALSE, TRUE, 'freely_available', FALSE, '2016-02-19T10:54:04', '2019-05-20T15:28:27');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100003, 'AllAfrica (en)', 'AllAfrica (sv)', 'AllAfrica''s news and information archive (which dates from 1996) is a distinctive collection containing articles and documents, in English and French, from allAfrica.com, the leading Africa information source on the Internet. AllAfrica collects and aggregates articles from over 100 African news organizations, as well as documents and releases from several hundred governmental, nongovernmental and international institutions, plus reporting from the AllAfrica news team. The Gothenburg University Library subscribes to the full archive, which is available only on allAfrica.com. Current news stories on the site (up to one-year old) are freely accessible. (en)', 'AllAfrica''s news and information archive (which dates from 1996) is a distinctive collection containing articles and documents, in English and French, from allAfrica.com, the leading Africa information source on the Internet. AllAfrica collects and aggregates articles from over 100 African news organizations, as well as documents and releases from several hundred governmental, nongovernmental and international institutions, plus reporting from the AllAfrica news team. The Gothenburg University Library subscribes to the full archive, which is available only on allAfrica.com. Current news stories on the site (up to one-year old) are freely accessible. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:54:04', '2021-06-18T10:12:02');
