INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100012, 'Arbline (en)', 'Arbline (sv)', 'Arbline är en katalog som täcker ämnesområdena arbetsliv, arbetsmiljö och arbetsmarknad. Den innehåller referenser till böcker, rapporter, konferenspublikationer, artiklar. (en)', 'Arbline är en katalog som täcker ämnesområdena arbetsliv, arbetsmiljö och arbetsmarknad. Den innehåller referenser till böcker, rapporter, konferenspublikationer, artiklar. (sv)', FALSE, '', '', FALSE, TRUE, 'freely_available', FALSE, '2016-02-19T10:54:04', '2019-07-19T10:08:17');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100013, 'Aristoteles Latinus database (en)', 'Aristoteles Latinus database (sv)', 'This database contains those texts that have been critically edited in the printed Aristoteles Latinus series. Other corpora complete the database, whether editions that have already been published or ones in preparation or unpublished, to produce the finished Aristoteles Latinus. The benefit of the electronic edition lies in the fact that it provides an integrated database of all the medieval translations of Aristotle''s work. The electronic database is not identical to the printed edition, as it omits the prefaces describing the manuscript tradition; nor does it include the apparatus of variant readings, the Greek-Latin comparative apparatus, or the bilingual indexes of the printed version. The texts included are prepared and supervised by the Aristoteles Latinus Centre of the Catholic University of Leuven, and produced in collaboration with the CTLO.
