INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100015, 'Oxford bibliographies: International relations (en)', 'Oxford bibliographies: International relations (sv)', 'International relations traditionally referred to the study of foreign affairs and political interaction between states. Today, the field covers the whole complex of cultural, economic, legal, military, and political relations of all states and their component populations, as well as non-state actors and international organizations. Oxford Bibliographies in International Relations offers a reliable, up-to-date, and authoritative guide to the best literature in the field. (en)', 'International relations traditionally referred to the study of foreign affairs and political interaction between states. Today, the field covers the whole complex of cultural, economic, legal, military, and political relations of all states and their component populations, as well as non-state actors and international organizations. Oxford Bibliographies in International Relations offers a reliable, up-to-date, and authoritative guide to the best literature in the field. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:54:04', '2021-07-27T10:13:37');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100016, 'Oxford bibliographies: Linguistics (en)', 'Oxford bibliographies: Linguistics (sv)', 'The study of linguistics makes connections across a wide array of scholarly concerns, from the humanities to many of the social and behavioral sciences to biology, physics, engineering, and even medicine. The study of language is central to a large number of rapidly advancing fields, such as neuroscience, cognitive science, and computer science. These overlapping domains make it challenging to stay informed about every applicable area and a great deal of this work has moved online with the most recent scholarship, research, and statistics appearing in online databases.
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100017, 'ARTbibliographies modern (en)', 'ARTbibliographies modern (sv)', 'This database is the only specialist bibliography available for the study of modern and contemporary art. Covers all art forms, from painting, sculpture and photography to video, body art and graffiti. Full abstracts and indexing from the late 1960s onwards. (en)', 'This database is the only specialist bibliography available for the study of modern and contemporary art. Covers all art forms, from painting, sculpture and photography to video, body art and graffiti. Full abstracts and indexing from the late 1960s onwards. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:54:04', '2021-07-27T10:15:41');
