INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100024, 'Store norske leksikon (en)', 'Store norske leksikon (sv)', 'Store norske leksikon består av kvalitetssäkrade artiklar och har varit Norges ledande uppslagsverk under hundra år. Kunnskapsforlaget har det redaktionella ansvaret för snl.no, och ett stort antal fackexperter ansvarar för kvaliteten för artiklarna. Litteraturlistorna redigeras genom bidrag från Norges nationalbibliotek. Enskilda användare kan också bidra med artiklar. (en)', 'Store norske leksikon består av kvalitetssäkrade artiklar och har varit Norges ledande uppslagsverk under hundra år. Kunnskapsforlaget har det redaktionella ansvaret för snl.no, och ett stort antal fackexperter ansvarar för kvaliteten för artiklarna. Litteraturlistorna redigeras genom bidrag från Norges nationalbibliotek. Enskilda användare kan också bidra med artiklar. (sv)', FALSE, '', '', FALSE, TRUE, 'freely_available', FALSE, '2016-02-19T10:54:04', '2019-05-24T12:32:00');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100026, 'AsiaPortal (en)', 'AsiaPortal (sv)', 'The AsiaPortal is a Nordic information portal for researchers, students, government officers and journalists with an interest in Asia. Its content is primarily based in the Social Sciences and Humanities. It is a collaborative endeavor between the 23 universities and research institutions in the Nordic NIAS Council and NIAS/NIAS LINC. The resource is available via SWAMID login. (en)', 'The AsiaPortal is a Nordic information portal for researchers, students, government officers and journalists with an interest in Asia. Its content is primarily based in the Social Sciences and Humanities. It is a collaborative endeavor between the 23 universities and research institutions in the Nordic NIAS Council and NIAS/NIAS LINC. The resource is available via SWAMID login. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg', FALSE, '2016-02-19T10:54:04', '2019-10-09T11:33:05');
