INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100031, 'Basque database (en)', 'Basque database (sv)', 'The database provides access to information about Basque topics written or produced after 1994. Core journals are indexed completely while other journal articles appear in the database when they fit the scope of the database. Formats included are articles, books, chapters of edited books, dissertations, and other publications. Included are Basque-related videos and DVDs, musical recordings, maps, and other media. (en)', 'The database provides access to information about Basque topics written or produced after 1994. Core journals are indexed completely while other journal articles appear in the database when they fit the scope of the database. Formats included are articles, books, chapters of edited books, dissertations, and other publications. Included are Basque-related videos and DVDs, musical recordings, maps, and other media. (sv)', FALSE, '', '', FALSE, TRUE, 'freely_available', FALSE, '2016-02-19T10:54:04', '2019-05-21T10:42:51');
