INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100049, 'Branschnyckeltal (en)', 'Branschnyckeltal (sv)', 'Drygt 40 olika nyckeltal för cirka 800 branscher som presenteras i form av median och kvartilvärden där jämförelse sker på branschnivå och antal anställda. Branschnyckeltalens population omfattar samtliga aktiebolag och ekonomiska föreningar som bedrivit verksamhet under referensåret exklusive finansiella företag såsom banker, investment- och försäkringsbolag. För att ett företag ska ingå i beräkningarna krävs en omsättning på minst 10 000 kronor. En mer ingående beskrivning av statistikens framtagande finns under rubriken Statistikens framtagande. (en)', 'Drygt 40 olika nyckeltal för cirka 800 branscher som presenteras i form av median och kvartilvärden där jämförelse sker på branschnivå och antal anställda. Branschnyckeltalens population omfattar samtliga aktiebolag och ekonomiska föreningar som bedrivit verksamhet under referensåret exklusive finansiella företag såsom banker, investment- och försäkringsbolag. För att ett företag ska ingå i beräkningarna krävs en omsättning på minst 10 000 kronor. En mer ingående beskrivning av statistikens framtagande finns under rubriken Statistikens framtagande. (sv)', FALSE, '', '', FALSE, TRUE, 'freely_available', FALSE, '2016-02-19T10:54:13', '2021-05-03T15:46:56');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100050, 'The Brill Dictionary of Religion (en)', 'The Brill Dictionary of Religion (sv)', 'The Brill Dictionary of Religion (BDR) Online addresses religion as an element of daily life and public discourse. It is richly illustrated and with more than 500 entries, the Brill Dictionary of Religion Online is a multi-media reference source on the many and various forms of religious commitment. The Brill Dictionary of Religion Online addresses the different theologies and doctrinal declarations of the official institutionalized religions and gives equal weight and consideration to a multiplicity of other religious phenomena. The Brill Dictionary of Religion Online helps map out and define the networks and connections created by various religions in contemporary societies, and provides models for understanding these complex phenomena. (en)', 'The Brill Dictionary of Religion (BDR) Online addresses religion as an element of daily life and public discourse. It is richly illustrated and with more than 500 entries, the Brill Dictionary of Religion Online is a multi-media reference source on the many and various forms of religious commitment. The Brill Dictionary of Religion Online addresses the different theologies and doctrinal declarations of the official institutionalized religions and gives equal weight and consideration to a multiplicity of other religious phenomena. The Brill Dictionary of Religion Online helps map out and define the networks and connections created by various religions in contemporary societies, and provides models for understanding these complex phenomena. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:54:13', '2021-07-27T11:08:48');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100052, 'Brill´s Encyclopaedia of the Neo-Latin World (en)', 'Brill´s Encyclopaedia of the Neo-Latin World (sv)', 'Brill’s Encyclopaedia of the Neo-Latin World traces the enduring history and broad cultural influence of Neo-Latin, the form of Latin that originated in the Italian Renaissance and persists to the modern era. Featuring original contributions by a host of distinguished international scholars, this work explores every aspect of the civilized world from literature and law to philosophy and the sciences. (en)', 'Brill’s Encyclopaedia of the Neo-Latin World traces the enduring history and broad cultural influence of Neo-Latin, the form of Latin that originated in the Italian Renaissance and persists to the modern era. Featuring original contributions by a host of distinguished international scholars, this work explores every aspect of the civilized world from literature and law to philosophy and the sciences. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:54:13', '2022-07-06T14:53:49');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100053, 'Brill''s Encyclopedia of Hinduism (en)', 'Brill''s Encyclopedia of Hinduism (sv)', 'Brill’s Encyclopedia of Hinduism presents the latest research on all the main aspects of the Hindu traditions. Its essays are original work written by the world’s foremost scholars on Hinduism. The encyclopedia aims at a balanced and even-handed view of Hinduism, recognizing the divergent perspectives and methods in the academic study of a religion that is both an ancient historical tradition and a flourishing tradition today. The encyclopedia embraces the greatest possible diversity, plurality, and heterogeneity, thus emphasizing that Hinduism encompasses a variety of regional traditions as well as a global world religion. (en)', 'Brill’s Encyclopedia of Hinduism presents the latest research on all the main aspects of the Hindu traditions. Its essays are original work written by the world’s foremost scholars on Hinduism. The encyclopedia aims at a balanced and even-handed view of Hinduism, recognizing the divergent perspectives and methods in the academic study of a religion that is both an ancient historical tradition and a flourishing tradition today. The encyclopedia embraces the greatest possible diversity, plurality, and heterogeneity, thus emphasizing that Hinduism encompasses a variety of regional traditions as well as a global world religion. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:54:13', '2022-07-06T14:55:56');
