INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100069, 'Music Online: Classical Music in Video (en)', 'Music Online: Classical Music in Video (sv)', 'Classical Music in Video is a comprehensive streaming video collection to support the study of classical music. The collection will contain performances of all forms of classical music, including major orchestral performances by leading orchestras, plus chamber music, oratorio, and solo performances, along with masterclasses and interviews with master teachers from around the world. (en)', 'Classical Music in Video is a comprehensive streaming video collection to support the study of classical music. The collection will contain performances of all forms of classical music, including major orchestral performances by leading orchestras, plus chamber music, oratorio, and solo performances, along with masterclasses and interviews with master teachers from around the world. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:54:13', '2021-10-01T11:14:46');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100070, 'Classical music library (en)', 'Classical music library (sv)', 'Classical Music Library is the world''s largest multi-label database of Classical music recordings for listening and learning in libraries. The collection includes recordings from the world''s greatest labels including Hyperion, Bridge Records, Sanctuary Classics, Artemis-Vanguard, Hänssler Classic, Vox and many more. Coverage includes music written from the earliest times (e.g. Gregorian Chant) to the present, including many contemporary composers. Repertoire ranges from vocal and choral music, to chamber, orchestral, solo instrumental, and opera. (en)', 'Classical Music Library is the world''s largest multi-label database of Classical music recordings for listening and learning in libraries. The collection includes recordings from the world''s greatest labels including Hyperion, Bridge Records, Sanctuary Classics, Artemis-Vanguard, Hänssler Classic, Vox and many more. Coverage includes music written from the earliest times (e.g. Gregorian Chant) to the present, including many contemporary composers. Repertoire ranges from vocal and choral music, to chamber, orchestral, solo instrumental, and opera. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:54:13', '2021-10-01T11:15:27');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100071, 'Classical Music Reference Library (en)', 'Classical Music Reference Library (sv)', 'Classical Music Reference Library spans the entire history of Western classical music. Included are the authoritative reference titles Baker''s Dictionary of Music, Baker''s Biographical Dictionary of Musicians, and Baker''s Student Encyclopedia of Music, which are available for the first time in electronic form. (en)', 'Classical Music Reference Library spans the entire history of Western classical music. Included are the authoritative reference titles Baker''s Dictionary of Music, Baker''s Biographical Dictionary of Musicians, and Baker''s Student Encyclopedia of Music, which are available for the first time in electronic form. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:54:13', '2021-10-01T11:16:05');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100072, 'Music Online: Classical Scores Library (en)', 'Music Online: Classical Scores Library (sv)', 'Classical Scores Library is a series of four volumes with a mission to provide a reliable and authoritative source for scores of the classical canon, as well as a resource for the discovery of lesser-known contemporary works. The collections encompass all major classical musical genres and time periods from the Middle Ages to the 21st century. With full, study, piano, and vocal scores, this comprehensive collection will enhance the study of music history, performance, composition and theory for a variety of scholars.
