INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100111, 'Early English Books Online (en)', 'Early English Books Online (sv)', 'Early English Books Online (EEBO) contains digital facsimile page images of virtually every work printed in England, Ireland, Scotland, Wales and British North America and works in English printed elsewhere from 1473-1700. Subject areas include English literature, history, philosophy, linguistics, theology, music, fine arts, education, mathematics, and science.
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100112, 'Early European books (en)', 'Early European books (sv)', 'Early European Books traces the history of printing in Europe from its origins through to the close of the seventeenth century, offering full-colour, high-resolution facsimile images of rare and hard-to-access printed sources.    Early European Books is the result of a cooperation between ProQuest and different university libraries in Europe. The first part contains scanned books from The Royal Library in Copenhagen, among others the works of Tycho Brahe. The second part contains scanned books from Biblioteca Nazionale Centrale di Firenze. (en)', 'Early European Books traces the history of printing in Europe from its origins through to the close of the seventeenth century, offering full-colour, high-resolution facsimile images of rare and hard-to-access printed sources.    Early European Books is the result of a cooperation between ProQuest and different university libraries in Europe. The first part contains scanned books from The Royal Library in Copenhagen, among others the works of Tycho Brahe. The second part contains scanned books from Biblioteca Nazionale Centrale di Firenze. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:54:21', '2022-08-25T15:21:03');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100115, 'EBSCO eBook collection (en)', 'EBSCO eBook collection (sv)', 'EBSCOhost eBook Collection, formerly NetLibrary, is a collection of full text ebooks. The University of Gothenburg has access to a large number of titles within all subject areas. Furthermore there is a collection of 3 500 classics freely accessible. (en)', 'EBSCOhost eBook Collection, formerly NetLibrary, is a collection of full text ebooks. The University of Gothenburg has access to a large number of titles within all subject areas. Furthermore there is a collection of 3 500 classics freely accessible. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:54:21', '2021-09-10T14:54:03');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100117, 'EconLit (en)', 'EconLit (sv)', 'References to economic literature: journal articles, books, theses, conference papers, working papers. Econlit adheres to the high quality standards of the Journal of Economic Literature (JEL) and is a source of citations and abstracts to economic research dating back to 1969. Provides comprehensive information on accounting, capital markets, econometrics, economic forecasting, government regulations, labour economics, monetary theory, urban economics and more. (en)', 'References to economic literature: journal articles, books, theses, conference papers, working papers. Econlit adheres to the high quality standards of the Journal of Economic Literature (JEL) and is a source of citations and abstracts to economic research dating back to 1969. Provides comprehensive information on accounting, capital markets, econometrics, economic forecasting, government regulations, labour economics, monetary theory, urban economics and more. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:54:21', '2021-06-21T11:22:03');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100118, 'The Economist Historical Archive  (en)', 'The Economist Historical Archive  (sv)', 'The Economist Historical Archive delivers a complete searchable copy of every issue of The Economist from 1843 to 2015. New full-colour images, multiple search indexes, exportable financial tables and a gallery of front covers highlighting a key topic of each week - all combine to offer a primary source of research covering the 19th and 20th centuries. Supplements, Special Reports and Surveys which appear regularly in The Economist are included.
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100119, 'EconPapers (en)', 'EconPapers (sv)', 'EconPapers provides access to RePEc, the world''s largest collection of on-line Economics working papers, journal articles and software. (en)', 'EconPapers provides access to RePEc, the world''s largest collection of on-line Economics working papers, journal articles and software. (sv)', FALSE, '', '', FALSE, TRUE, 'freely_available', FALSE, '2016-02-19T10:54:21', '2019-05-31T09:52:04');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100120, 'ECOTOX (en)', 'ECOTOX (sv)', 'The ECOTOX (ECOTOXicology) database provides single chemical toxicity information for aquatic and terrestrial life. ECOTOX is a useful tool for examining impacts of chemicals on the environment. Peer-reviewed literature is the primary source of information encoded in the database. Pertinent information on the species, chemical, test methods, and results presented by the author(s) are abstracted and entered into the database. Another source of test results is independently compiled data files provided by various United States and International government agencies. (en)', 'The ECOTOX (ECOTOXicology) database provides single chemical toxicity information for aquatic and terrestrial life. ECOTOX is a useful tool for examining impacts of chemicals on the environment. Peer-reviewed literature is the primary source of information encoded in the database. Pertinent information on the species, chemical, test methods, and results presented by the author(s) are abstracted and entered into the database. Another source of test results is independently compiled data files provided by various United States and International government agencies. (sv)', FALSE, '', '', FALSE, TRUE, 'freely_available', FALSE, '2016-02-19T10:54:21', '2019-05-31T10:07:05');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100121, 'EDGAR (en)', 'EDGAR (sv)', 'The EDGAR database contains the electronic filings of US public companies to the Securities and Exchange Commission. Allows you to retrieve real-time filings for a specific company and to find key company information including its name, address, telephone number, state of incorporation, Central Index Key (CIK) number, Standard Industrial Classification (SIC) code, and fiscal year end. (en)', 'The EDGAR database contains the electronic filings of US public companies to the Securities and Exchange Commission. Allows you to retrieve real-time filings for a specific company and to find key company information including its name, address, telephone number, state of incorporation, Central Index Key (CIK) number, Standard Industrial Classification (SIC) code, and fiscal year end. (sv)', FALSE, '', '', FALSE, TRUE, 'freely_available', FALSE, '2016-02-19T10:54:21', '2019-05-31T10:14:32');
