INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100131, 'Encyclopaedia of Islam, Second edition (en)', 'Encyclopaedia of Islam, Second edition (sv)', 'The Encyclopaedia of Islam (Second Edition) Online sets out the present state of our knowledge of the Islamic World. It is a unique and invaluable reference tool, an essential key to understanding the world of Islam, and the authoritative source not only for the religion, but also for the believers and the countries in which they live. (en)', 'The Encyclopaedia of Islam (Second Edition) Online sets out the present state of our knowledge of the Islamic World. It is a unique and invaluable reference tool, an essential key to understanding the world of Islam, and the authoritative source not only for the religion, but also for the believers and the countries in which they live. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:54:21', '2021-07-28T11:58:58');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100132, 'Encyclopaedia of Islam, Second Edition, Glossary and Index of Terms (en)', 'Encyclopaedia of Islam, Second Edition, Glossary and Index of Terms (sv)', 'The online versions of Encyclopaedia of Islam are already full-text searchable and browsable through alphabetical lists of entries.  The Glossary and Index of Terms is an additional finding aid to Encyclopaedia of Islam , Second edition.  Presented for convenience as a separate data set, the Glossary is an invaluable source of information for both the non-specialist, who is given an insight into the varied and rich vocabulary of Islam, and the specialist, who may discover surprising new contexts in which a term is being used. Links to Encyclopaedia of Islam entries and suggestions for related content are also provided. (en)', 'The online versions of Encyclopaedia of Islam are already full-text searchable and browsable through alphabetical lists of entries.  The Glossary and Index of Terms is an additional finding aid to Encyclopaedia of Islam , Second edition.  Presented for convenience as a separate data set, the Glossary is an invaluable source of information for both the non-specialist, who is given an insight into the varied and rich vocabulary of Islam, and the specialist, who may discover surprising new contexts in which a term is being used. Links to Encyclopaedia of Islam entries and suggestions for related content are also provided. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:54:21', '2021-07-28T13:26:57');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100133, 'Encyclopaedia of Islam, three (en)', 'Encyclopaedia of Islam, three (sv)', 'The Encyclopaedia of Islam sets out the present state of our knowledge of the Islamic World. It embraces articles on distinguished Muslims of every age and land, on tribes and dynasties, on the crafts and sciences, on political and religious institutions, on the geography, ethnography, flora and fauna of the various countries and on the history, topography and monuments of the major towns and cities. In its geographical and historical scope it encompasses the old Arabo-Islamic empire, the Islamic countries of Iran, Central Asia, the Indian sub-continent and Indonesia, the Ottoman Empire and all other Islamic countries. (en)', 'The Encyclopaedia of Islam sets out the present state of our knowledge of the Islamic World. It embraces articles on distinguished Muslims of every age and land, on tribes and dynasties, on the crafts and sciences, on political and religious institutions, on the geography, ethnography, flora and fauna of the various countries and on the history, topography and monuments of the major towns and cities. In its geographical and historical scope it encompasses the old Arabo-Islamic empire, the Islamic countries of Iran, Central Asia, the Indian sub-continent and Indonesia, the Ottoman Empire and all other Islamic countries. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:54:21', '2021-07-28T13:28:20');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100134, 'Encyclopaedia of Judaism (en)', 'Encyclopaedia of Judaism (sv)', 'The Encyclopaedia of Judaism Online offers an authoritative, comprehensive, and systematic presentation of the current state of scholarship on fundamental issues of Judaism, both past and present. While heavy emphasis is placed on the classical literature of Judaism and its history, the Encyclopaedia of Judaism Online also includes principal entries on circumcision, genetic engineering, homosexuality, intermarriage in American Judaism, and other acutely contemporary issues. Comprehensive and up-to-date, it reflects the highest standards in scholarship. Covering a tradition of nearly four thousand years, some of the most distinguished scholars in the field describe the way of life, history, art, theology, philosophy, and the practices and beliefs of the Jewish people. (en)', 'The Encyclopaedia of Judaism Online offers an authoritative, comprehensive, and systematic presentation of the current state of scholarship on fundamental issues of Judaism, both past and present. While heavy emphasis is placed on the classical literature of Judaism and its history, the Encyclopaedia of Judaism Online also includes principal entries on circumcision, genetic engineering, homosexuality, intermarriage in American Judaism, and other acutely contemporary issues. Comprehensive and up-to-date, it reflects the highest standards in scholarship. Covering a tradition of nearly four thousand years, some of the most distinguished scholars in the field describe the way of life, history, art, theology, philosophy, and the practices and beliefs of the Jewish people. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:54:21', '2021-07-28T13:34:08');
