INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100142, 'Encyclopedia of Arabic language and linguistics (en)', 'Encyclopedia of Arabic language and linguistics (sv)', 'Encyclopedia of Arabic language and linguistics Online comprehensively covers all aspects of Arabic languages and linguistics. It is interdisciplinary in scope and represents different schools and approaches in order to be as objective and versatile as possible. The Encyclopedia of Arabic Language and Linguistics Online is cross-searchable and cross-referenced, and is equipped with a browsable index. All relevant fields in Arabic linguistics, both general and language specific are covered and the Encyclopedia of Arabic Language and Linguistics Online includes topics from interdisciplinary fields, such as anthropology, psychology, sociology, philosophy, and computer science. (en)', 'Encyclopedia of Arabic language and linguistics Online comprehensively covers all aspects of Arabic languages and linguistics. It is interdisciplinary in scope and represents different schools and approaches in order to be as objective and versatile as possible. The Encyclopedia of Arabic Language and Linguistics Online is cross-searchable and cross-referenced, and is equipped with a browsable index. All relevant fields in Arabic linguistics, both general and language specific are covered and the Encyclopedia of Arabic Language and Linguistics Online includes topics from interdisciplinary fields, such as anthropology, psychology, sociology, philosophy, and computer science. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:54:30', '2021-07-28T13:46:53');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100143, 'Encyclopedia of Christianity Online (en)', 'Encyclopedia of Christianity Online (sv)', 'The Encyclopedia of Christianity Online describes modern-day Christian beliefs and communities in the context of 2000 years of apostolic tradition and Christian history. It is based on the third, revised edition of the German work Evangelisches Kirchenlexikon. The Encyclopedia of Christianity Online includes all 5 volumes of the print edition of 1999-2008 which has become a standard reference work for the study of Christianity past and present. The Encyclopedia of Christianity Online also looks outward beyond Christianity, considering other world religions and philosophies as it paints the overall religious and socio-cultural picture in which the Christianity finds itself. (en)', 'The Encyclopedia of Christianity Online describes modern-day Christian beliefs and communities in the context of 2000 years of apostolic tradition and Christian history. It is based on the third, revised edition of the German work Evangelisches Kirchenlexikon. The Encyclopedia of Christianity Online includes all 5 volumes of the print edition of 1999-2008 which has become a standard reference work for the study of Christianity past and present. The Encyclopedia of Christianity Online also looks outward beyond Christianity, considering other world religions and philosophies as it paints the overall religious and socio-cultural picture in which the Christianity finds itself. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:54:30', '2021-07-28T14:03:52');
