INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100148, 'Encyclopedia of fish physiology (en)', 'Encyclopedia of fish physiology (sv)', 'This encyclopedia covers the diversity of fish physiology in over 300 articles and provides entry level information for students and summary overviews for researchers alike. Broadly organised into four themes, articles cover Functional, Thematic, and Phylogenetic Physiology, and Fish Genomics. (en)', 'This encyclopedia covers the diversity of fish physiology in over 300 articles and provides entry level information for students and summary overviews for researchers alike. Broadly organised into four themes, articles cover Functional, Thematic, and Phylogenetic Physiology, and Fish Genomics. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:54:30', '2021-07-28T14:05:18');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100149, 'Encyclopedia of global health (en)', 'Encyclopedia of global health (sv)', 'The Encyclopedia of Global Health is a comprehensive A to Z, interdisciplinary, one-stop reference to a broad array of health topics worldwide. It covers all aspects of health including physical and mental health entries, current health status in each country of the five continents, biographies of major doctors and researchers, profiles of major medical institutes, organizations, corporations, and foundations, descriptions of major drugs and operations, articles on national health policies, and thematic health topics in the humanities. Pedagogical elements of the encyclopedia include an in-depth chronology detailing advances in health through history, a glossary of health definitions, extensive cross-references to related topics, and thorough bibliographic citations. (en)', 'The Encyclopedia of Global Health is a comprehensive A to Z, interdisciplinary, one-stop reference to a broad array of health topics worldwide. It covers all aspects of health including physical and mental health entries, current health status in each country of the five continents, biographies of major doctors and researchers, profiles of major medical institutes, organizations, corporations, and foundations, descriptions of major drugs and operations, articles on national health policies, and thematic health topics in the humanities. Pedagogical elements of the encyclopedia include an in-depth chronology detailing advances in health through history, a glossary of health definitions, extensive cross-references to related topics, and thorough bibliographic citations. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:54:30', '2021-08-10T11:21:01');
