INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100175, 'European research papers archive (en)', 'European research papers archive (sv)', 'ERPA''s aim is to provide a common access point for the online working paper series of the participating institutions in order to help researchers in the field of European Integration studies searching the growing number of working papers now available in the internet. ERPA is confined to high quality series in order to guarantee high standards. The participating series editors achieve this high level of scholarship by different means: while the Academy of European Law, the Max Planck Institute, and the Harvard Jean Monnet Chair practise high-level internal refereeing, the Robert Schuman Centre and the EIoP have a double-blind review process with two referees per paper. (en)', 'ERPA''s aim is to provide a common access point for the online working paper series of the participating institutions in order to help researchers in the field of European Integration studies searching the growing number of working papers now available in the internet. ERPA is confined to high quality series in order to guarantee high standards. The participating series editors achieve this high level of scholarship by different means: while the Academy of European Law, the Max Planck Institute, and the Harvard Jean Monnet Chair practise high-level internal refereeing, the Robert Schuman Centre and the EIoP have a double-blind review process with two referees per paper. (sv)', FALSE, '', '', FALSE, TRUE, 'freely_available', FALSE, '2016-02-19T10:54:30', '2019-06-05T13:45:44');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100176, 'European views of the Americas, 1493-1750 (en)', 'European views of the Americas, 1493-1750 (sv)', 'This bibliographic database is a valuable index for libraries, scholars and individuals interested in European works that relate to the Americas. The database is a comprehensive guide to printed records about the Americas written in Europe before 1750. It covers the history of European exploration as well as portrayals of Native American peoples. A wide range of subject areas are covered; from natural disasters to disease outbreaks and slavery. (en)', 'This bibliographic database is a valuable index for libraries, scholars and individuals interested in European works that relate to the Americas. The database is a comprehensive guide to printed records about the Americas written in Europe before 1750. It covers the history of European exploration as well as portrayals of Native American peoples. A wide range of subject areas are covered; from natural disasters to disease outbreaks and slavery. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:54:30', '2021-06-21T14:51:49');
