INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100225, 'HEDBIB International Bibliographic Database on Higher Education (en)', 'HEDBIB International Bibliographic Database on Higher Education (sv)', 'HEDBIB is a database on higher education systems, administration, planning, policy, and evaluation. It has been maintained by the IAU, International Association of Universities, since 1988 with contributions from UNESCO Headquarters; the European Centre for Higher Education (CEPES); UNESCO International Institute for Educational Planning (IIEP); UNESCO International Institute for Higher Education in Latin America (IESALC); the American Council on Education (ACE). In 2012, the following IAU Member organisations have become contributing partners to HEDBIB: Agence universitaire de la Francophonie (AUF), Associació Catalana d''Universitats Públiques (ACUP), Higher Education South Africa (HESA), Southern African Regional Universities Association (SARUA), Union de Universidades de América Latina (UDUAL). (en)', 'HEDBIB is a database on higher education systems, administration, planning, policy, and evaluation. It has been maintained by the IAU, International Association of Universities, since 1988 with contributions from UNESCO Headquarters; the European Centre for Higher Education (CEPES); UNESCO International Institute for Educational Planning (IIEP); UNESCO International Institute for Higher Education in Latin America (IESALC); the American Council on Education (ACE). In 2012, the following IAU Member organisations have become contributing partners to HEDBIB: Agence universitaire de la Francophonie (AUF), Associació Catalana d''Universitats Públiques (ACUP), Higher Education South Africa (HESA), Southern African Regional Universities Association (SARUA), Union de Universidades de América Latina (UDUAL). (sv)', FALSE, '', '', FALSE, TRUE, 'freely_available', FALSE, '2016-02-19T10:54:38', '2021-04-12T15:04:44');
