INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100236, 'IMDb (en)', 'IMDb (sv)', 'The Internet Movie Database (IMDb) is a collection of movie information. IMDb tries to catalogue every pertinent detail about a movie, from who was in it, to who made it, to trivia about it, to filming locations, and even where one can find reviews and fan sites on the web. (en)', 'The Internet Movie Database (IMDb) is a collection of movie information. IMDb tries to catalogue every pertinent detail about a movie, from who was in it, to who made it, to trivia about it, to filming locations, and even where one can find reviews and fan sites on the web. (sv)', FALSE, '', '', FALSE, TRUE, 'freely_available', FALSE, '2016-02-19T10:54:38', '2019-06-17T11:50:47');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100237, 'IMF eLibrary (en)', 'IMF eLibrary (sv)', 'The International Monetary Fund (IMF) provides statistics on many aspects of international finance going back to 1948. The IMF eLibrary gives direct access to the IMF´s periodicals, books, working papers, studies and statistical tools. You will find information and perspective on macroeconomics, globalization, development, trade and aid, technical assistance, demographics, emerging markets, policy advice, poverty reduction, and much more. The statistical database from the International Monetary Fund, includes four of the IMF''s core databases: Balance of Payment Statistics (BOPS), Direction of Trade Statistics (DOTS), Government Finance Statistics (GFS) and International Financial Statistics (IFS). (en)', 'The International Monetary Fund (IMF) provides statistics on many aspects of international finance going back to 1948. The IMF eLibrary gives direct access to the IMF´s periodicals, books, working papers, studies and statistical tools. You will find information and perspective on macroeconomics, globalization, development, trade and aid, technical assistance, demographics, emerging markets, policy advice, poverty reduction, and much more. The statistical database from the International Monetary Fund, includes four of the IMF''s core databases: Balance of Payment Statistics (BOPS), Direction of Trade Statistics (DOTS), Government Finance Statistics (GFS) and International Financial Statistics (IFS). (sv)', FALSE, '', '', FALSE, TRUE, 'freely_available', FALSE, '2016-02-19T10:54:38', '2020-02-04T12:31:31');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100238, 'Index Islamicus (en)', 'Index Islamicus (sv)', 'This database is a comprehensive index to publications on Islamic subjects throughout the world. Records cover almost 100 years, from 1906 to the present. The scope includes the Middle East, the Muslim areas of Asia and Africa, plus Muslim minorities elsewhere in the world. Over 3,000 journals are monitored for inclusion, together with conference proceedings, monographs, multi-authored works and book reviews. Journals and books are indexed down to the article and chapter level. (en)', 'This database is a comprehensive index to publications on Islamic subjects throughout the world. Records cover almost 100 years, from 1906 to the present. The scope includes the Middle East, the Muslim areas of Asia and Africa, plus Muslim minorities elsewhere in the world. Over 3,000 journals are monitored for inclusion, together with conference proceedings, monographs, multi-authored works and book reviews. Journals and books are indexed down to the article and chapter level. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:54:38', '2022-06-08T14:37:41');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100240, 'Index of Botanical Publications (en)', 'Index of Botanical Publications (sv)', 'For the purpose of verifying publication names in the Specimen Database and the Gray Index, the Herbaria maintain an index of botanical publications. It is a unique compendium of bibliographic references for both books and journals, dependent primarily on the standard print sources for botanical literature. (en)', 'For the purpose of verifying publication names in the Specimen Database and the Gray Index, the Herbaria maintain an index of botanical publications. It is a unique compendium of bibliographic references for both books and journals, dependent primarily on the standard print sources for botanical literature. (sv)', FALSE, '', '', FALSE, TRUE, 'freely_available', FALSE, '2016-02-19T10:54:38', '2019-05-27T16:01:58');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100241, 'Index to foreign legal periodicals (IFLP) (en)', 'Index to foreign legal periodicals (IFLP) (sv)', 'Produced by the American Association of Law Libraries, the Index to Foreign Legal Periodicals (IFLP) is the preeminent multilingual index to articles and book reviews appearing in over 500 legal journals published worldwide. It provides in-depth coverage of public and private international law, comparative and foreign law, and the law of all jurisdictions other than the United States, the U.K., Canada, and Australia. IFLP also analyses the contents of approximately eighty individually published collections of legal essays, Festschriften, Mélanges, and congress reports each year.
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100243, 'Index Translationum (en)', 'Index Translationum (sv)', 'Contains cumulative bibliographical information on books translated and published in about a hundred of UNESCO´s member states since 1979 and totals more than two million entries in all disciplines: literature, social and human sciences, natural and exact sciences, art, history and so forth. The references registered before 1979 can be found in the printed editions of the Index Translationum, available in all National depository libraries and at the UNESCO library in Paris. (en)', 'Contains cumulative bibliographical information on books translated and published in about a hundred of UNESCO´s member states since 1979 and totals more than two million entries in all disciplines: literature, social and human sciences, natural and exact sciences, art, history and so forth. The references registered before 1979 can be found in the printed editions of the Index Translationum, available in all National depository libraries and at the UNESCO library in Paris. (sv)', FALSE, '', '', FALSE, TRUE, 'freely_available', FALSE, '2016-02-19T10:54:46', '2019-05-27T16:04:14');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100244, 'Indo-European etymological dictionaries online (en)', 'Indo-European etymological dictionaries online (sv)', 'The Indo-European Etymological Dictionaries Online (IEDO) reconstructs the lexicon for the most important languages and language branches of Indo-European. It is a rich and voluminous online reference source for historical and general linguists. Dictionaries can be cross-searched, with an advance search for each individual dictionary enabling the user to perform more complex research queries. Each entry is accompanied by grammatical info, meaning(s), etymological commentary, reconstructions, cognates and often extensive bibliographical information. (en)', 'The Indo-European Etymological Dictionaries Online (IEDO) reconstructs the lexicon for the most important languages and language branches of Indo-European. It is a rich and voluminous online reference source for historical and general linguists. Dictionaries can be cross-searched, with an advance search for each individual dictionary enabling the user to perform more complex research queries. Each entry is accompanied by grammatical info, meaning(s), etymological commentary, reconstructions, cognates and often extensive bibliographical information. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:54:46', '2022-07-06T15:00:00');
