INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100246, 'INNOVA (en)', 'INNOVA (sv)', 'En LIBRIS-databas som omfattar litteratur framförallt inom ämnesområdet immateriellt rättsskydd, det vill säga patent-, varumärkes-, mönster-, namn- och upphovsrätt och innehåller referenser till tidskriftsartiklar, monografier, konferenstryck och patentverkspublikationer. (en)', 'En LIBRIS-databas som omfattar litteratur framförallt inom ämnesområdet immateriellt rättsskydd, det vill säga patent-, varumärkes-, mönster-, namn- och upphovsrätt och innehåller referenser till tidskriftsartiklar, monografier, konferenstryck och patentverkspublikationer. (sv)', FALSE, '', '', FALSE, TRUE, 'freely_available', FALSE, '2016-02-19T10:54:46', '2019-06-17T13:48:39');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100247, 'Integrated taxonomic information system (en)', 'Integrated taxonomic information system (sv)', 'The ITIS includes documented taxonomic information of flora and fauna from both aquatic and terrestrial habitats. For each scientific name, ITIS will include the authority (author and date), taxonomic rank, associated synonyms and vernacular names where available, a unique taxonomic serial number, data source information (publications, experts, etc.) and data quality indicators. (en)', 'The ITIS includes documented taxonomic information of flora and fauna from both aquatic and terrestrial habitats. For each scientific name, ITIS will include the authority (author and date), taxonomic rank, associated synonyms and vernacular names where available, a unique taxonomic serial number, data source information (publications, experts, etc.) and data quality indicators. (sv)', FALSE, '', '', FALSE, TRUE, 'freely_available', FALSE, '2016-02-19T10:54:46', '2019-06-03T10:29:21');
