INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100249, 'International bibliography of art (en)', 'International bibliography of art (sv)', 'The International Bibliography of Art (IBA) is a resource for scholarly literature on Western art. IBA is the successor to the Bibliography of the History of Art (BHA), and retains the editorial policies which made BHA one of the most trusted and frequently consulted sources in the field. The database includes records created by the Getty Research Institute in 2008-09, with new records created by ProQuest using the same thesaurus and authority files. (en)', 'The International Bibliography of Art (IBA) is a resource for scholarly literature on Western art. IBA is the successor to the Bibliography of the History of Art (BHA), and retains the editorial policies which made BHA one of the most trusted and frequently consulted sources in the field. The database includes records created by the Getty Research Institute in 2008-09, with new records created by ProQuest using the same thesaurus and authority files. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:54:46', '2022-06-08T14:31:01');
