INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100256, 'International encyclopedia of organization studies (en)', 'International encyclopedia of organization studies (sv)', 'Organization studies is the examination of how individuals construct organizational structures, processes, and practices and how these, in turn, shape social relations and create institutions that ultimately influence people. (en)', 'Organization studies is the examination of how individuals construct organizational structures, processes, and practices and how these, in turn, shape social relations and create institutions that ultimately influence people. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:54:46', '2021-08-10T12:22:56');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100257, 'International encyclopedia of political science (en)', 'International encyclopedia of political science (sv)', 'With entries from leading international scholars from around the world, this title provides a definitive, comprehensive picture o all aspects of political life, recognizing its theoretical foundations and including empirical findings from across the globe. (en)', 'With entries from leading international scholars from around the world, this title provides a definitive, comprehensive picture o all aspects of political life, recognizing its theoretical foundations and including empirical findings from across the globe. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:54:46', '2021-08-10T12:24:20');
