INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100259, 'International encyclopedia of the social & behavioral sciences (en)', 'International encyclopedia of the social & behavioral sciences (sv)', 'This Encyclopedia is the first attempt in a generation to map the social and behavioral sciences on a grand scale. Available in both print (26 volumes) and online editions, it comprises 4,000 articles and includes 90,000 bibliographic references as well as comprehensive name and subject indexes. (en)', 'This Encyclopedia is the first attempt in a generation to map the social and behavioral sciences on a grand scale. Available in both print (26 volumes) and online editions, it comprises 4,000 articles and includes 90,000 bibliographic references as well as comprehensive name and subject indexes. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:54:46', '2021-08-03T13:04:37');
