INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100264, 'The internet encyclopedia of philosophy (en)', 'The internet encyclopedia of philosophy (sv)', 'The Internet Encyclopedia of Philosophy (IEP) provides access to detailed, scholarly information on key topics and philosophers in all areas of philosophy. The Encyclopedia receives no funding, and operates through the volunteer work of the editors, authors, volunteers, and technical advisers. Most of the articles in The IEP are original contributions by specialized philosophers; others are temporary, or "proto articles", and have largely been adapted from older sources. (en)', 'The Internet Encyclopedia of Philosophy (IEP) provides access to detailed, scholarly information on key topics and philosophers in all areas of philosophy. The Encyclopedia receives no funding, and operates through the volunteer work of the editors, authors, volunteers, and technical advisers. Most of the articles in The IEP are original contributions by specialized philosophers; others are temporary, or "proto articles", and have largely been adapted from older sources. (sv)', FALSE, '', '', FALSE, TRUE, 'freely_available', FALSE, '2016-02-19T10:54:46', '2019-05-31T15:09:58');
