INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100269, 'JoVE (en)', 'JoVE (sv)', 'JoVE (Journal of Visualized Experiments) publishes peer-reviewed scientific experiments in video articles. Step-by-step demonstrations offer the possibility of recreating experiments in different disciplines. The JoVE Science Education video database features videos demonstrating essential scientific concepts and experimental methods.
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100270, 'JSTOR global plants (en)', 'JSTOR global plants (sv)', 'JSTOR global plants offers access to botanical resources from dozens of herbaria, libraries, museums and other research institutions. The database includes plant type specimens from herbaria around the world, scientific research articles and correspondence dating back hundreds of years, and full-text books and reference works on botany. (en)', 'JSTOR global plants offers access to botanical resources from dozens of herbaria, libraries, museums and other research institutions. The database includes plant type specimens from herbaria around the world, scientific research articles and correspondence dating back hundreds of years, and full-text books and reference works on botany. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:54:46', '2022-06-22T13:44:51');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100271, 'JSTOR (en)', 'JSTOR (sv)', 'JSTOR (Journal storage) is an electronic full text archive containing important journals covering several subject areas. The journals are stored from Volume I, Issue 1 to the "moving wall". The moving wall represents the time period between the last issue available in JSTOR and the most recently published issue of a journal. It is specified by publishers in their license agreements with JSTOR, and generally ranges from 3 and 5 years. The following collections are available: Arts & Sciences Collection I-III and VII,  Business Collection, Ecology & Botany Collection. (en)', 'JSTOR (Journal storage) is an electronic full text archive containing important journals covering several subject areas. The journals are stored from Volume I, Issue 1 to the "moving wall". The moving wall represents the time period between the last issue available in JSTOR and the most recently published issue of a journal. It is specified by publishers in their license agreements with JSTOR, and generally ranges from 3 and 5 years. The following collections are available: Arts & Sciences Collection I-III and VII,  Business Collection, Ecology & Botany Collection. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:54:46', '2022-06-22T13:42:15');
