INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100290, 'Legislative Observatory (en)', 'Legislative Observatory (sv)', 'The Legislative Observatory database was set up in 1994 as a tool for monitoring the EU institutional decision-making process, with a particular focus on the European Parliament''s role. The website has now expanded to include more information on Parliament''s activities, involving not only its legislative but also its budgetary powers, its right of initiative, its power to endorse appointments, etc.    The Legislative Observatory publishes comprehensive records in English and French, known as "procedure files". Each file has a unique identifying reference number and is constantly updated as the procedure progresses, until it is completed. (en)', 'The Legislative Observatory database was set up in 1994 as a tool for monitoring the EU institutional decision-making process, with a particular focus on the European Parliament''s role. The website has now expanded to include more information on Parliament''s activities, involving not only its legislative but also its budgetary powers, its right of initiative, its power to endorse appointments, etc.    The Legislative Observatory publishes comprehensive records in English and French, known as "procedure files". Each file has a unique identifying reference number and is constantly updated as the procedure progresses, until it is completed. (sv)', FALSE, '', '', FALSE, TRUE, 'freely_available', FALSE, '2016-02-19T10:54:46', '2019-05-17T13:42:27');
