INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100293, 'Lexin (en)', 'Lexin (sv)', 'Lexin står för lexikon för invandrare och startades av Statens institut för läromedel och Statens invandrarverk. Lexikonen är speciellt anpassade för att användas i undervisningen i svenska som andraspråk och innehåller därför bara de vanligaste orden i svenskan. Lexin finns på Albanska, arabiska, bosniska, finska, grekiska, kroatiska, makedoniska, nordkurdiska, persiska, romska (arli), rumänska, serbiska, somaliska, spanska, svenska, sydkurdiska, tigrinska, turkiska, turabdinska och vietnamesiska. (en)', 'Lexin står för lexikon för invandrare och startades av Statens institut för läromedel och Statens invandrarverk. Lexikonen är speciellt anpassade för att användas i undervisningen i svenska som andraspråk och innehåller därför bara de vanligaste orden i svenskan. Lexin finns på Albanska, arabiska, bosniska, finska, grekiska, kroatiska, makedoniska, nordkurdiska, persiska, romska (arli), rumänska, serbiska, somaliska, spanska, svenska, sydkurdiska, tigrinska, turkiska, turabdinska och vietnamesiska. (sv)', FALSE, '', '', FALSE, TRUE, 'freely_available', FALSE, '2016-02-19T10:54:54', '2019-05-21T09:47:44');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100294, 'Library, information science & technology abstracts (en)', 'Library, information science & technology abstracts (sv)', 'Database about libraries and information management. Bibliographic database with coverage on subjects such as librarianship, classification, cataloguing, bibliometrics, online information retrieval, information management and more. Indexes more than 600 periodicals plus books, research reports, and proceedings. (en)', 'Database about libraries and information management. Bibliographic database with coverage on subjects such as librarianship, classification, cataloguing, bibliometrics, online information retrieval, information management and more. Indexes more than 600 periodicals plus books, research reports, and proceedings. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:54:54', '2021-06-21T15:14:50');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100295, 'Library of Latin texts (en)', 'Library of Latin texts (sv)', 'Library of Latin Texts – online,  LLT,  is a database for Latin texts. It contains texts from the beginning of Latin literature (Livius Andronicus, 240 BC) through to the texts of the Second Vatican Council (1962-1965). The texts have been taken from the Corpus Christianorum series and from many other leading editions.
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100298, 'Literature, arts, and medicine database (en)', 'Literature, arts, and medicine database (sv)', 'An annotated bibliography of prose, poetry, film, video and art which was developed to be resource in MEDICAL HUMANITIES, for use in health/pre-health and liberal arts settings. (en)', 'An annotated bibliography of prose, poetry, film, video and art which was developed to be resource in MEDICAL HUMANITIES, for use in health/pre-health and liberal arts settings. (sv)', FALSE, '', '', FALSE, TRUE, 'freely_available', FALSE, '2016-02-19T10:54:54', '2019-05-21T11:20:33');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100299, 'Literature Online (en)', 'Literature Online (sv)', 'Literature Online contains a full library of up-to-date, fully searchable criticism and reference resources in addition to the full text of poetry, drama, and prose fiction from the eighth century to the present day.  It provides the materials for the historical study of almost every period and genre of literature in English and features increasing numbers of works by the major authors of the twentieth century.  Contemporary criticism is available through the full text of current literature journals that can be accessed through the Annual Bibliography of English Language and Literature (ABELL). Linking to Early English Books Online is enabled.
