INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100309, 'Oxford bibliographies: Biblical studies (en)', 'Oxford bibliographies: Biblical studies (sv)', 'The modern academic study of the Bible is a highly technical and multifaceted field. Its practitioners are often required to gain expertise in diverse areas ranging from archaeology, Egyptology, Assyriology, and linguistics through textual, historical, and sociological studies, to literary theory, feminist studies, philosophy, and theology – to name but a few. As a result, the field of Biblical Studies is incredibly dynamic, with new discoveries, new methodologies, and new perspectives continually being brought to bear on the interpretation of the Bible.
