INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100315, 'Mediearkivet (en)', 'Mediearkivet (sv)', 'Tidningsartiklar i fulltext, publicerade efter 1981. Mediearkivet är Nordens största digitala nyhetsarkiv innehållande tryckta dagstidningar, tidskrifter och affärspress. I Mediearkivet kan du söka artiklar på ett och samma ställe från alla stora dagstidningar, landsortstidningar och hundratals tidskrifter. Dessutom finns tillgång till ytterligare mediekanaler som kompletterar det tryckta materialet: svenskt webbarkiv, bloggar och TV/radio.
