INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100319, 'MLA international bibliography (en)', 'MLA international bibliography (sv)', 'The MLA International Bibliography is a subject index for books, articles and websites published on modern languages, literatures, folklore, and linguistics. It is produced by the Modern Language Association (MLA), an organization dedicated to the study and teaching of language and literature. The electronic version of the Bibliography dates back to 1925 and contains over two million citations from more than 4,400 periodicals (including peer-reviewed e-journals) and 1,000 book publishers. It is compiled by the staff of the MLA Department of Bibliographic Information Services with the cooperation of more than 100 contributing bibliographers in the United States and abroad. Such international coverage is represented by literature from Africa, Asia, Australia, Europe, and North and South America, and while the majority of records are from English-language publications, at least sixty other languages are represented including French, Spanish, German, Russian, Portuguese, Norwegian, and Swedish.    (en)', 'The MLA International Bibliography is a subject index for books, articles and websites published on modern languages, literatures, folklore, and linguistics. It is produced by the Modern Language Association (MLA), an organization dedicated to the study and teaching of language and literature. The electronic version of the Bibliography dates back to 1925 and contains over two million citations from more than 4,400 periodicals (including peer-reviewed e-journals) and 1,000 book publishers. It is compiled by the staff of the MLA Department of Bibliographic Information Services with the cooperation of more than 100 contributing bibliographers in the United States and abroad. Such international coverage is represented by literature from Africa, Asia, Australia, Europe, and North and South America, and while the majority of records are from English-language publications, at least sixty other languages are represented including French, Spanish, German, Russian, Portuguese, Norwegian, and Swedish.    (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:54:54', '2021-06-21T15:21:14');
