INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100321, 'Oxford bibliographies: Childhood studies (en)', 'Oxford bibliographies: Childhood studies (sv)', 'The scholarly study of children and young people is a relatively new multidisciplinary effort that spans multiple epistemologies and methodologies, making it challenging for students and scholars to stay informed. From psychology to labor rights, from ethics to education.  Childhood Studies is international and cross-cultural in scope, transcending narrow geographical confines and analyzing modern and historical childhoods both locally and globally. (en)', 'The scholarly study of children and young people is a relatively new multidisciplinary effort that spans multiple epistemologies and methodologies, making it challenging for students and scholars to stay informed. From psychology to labor rights, from ethics to education.  Childhood Studies is international and cross-cultural in scope, transcending narrow geographical confines and analyzing modern and historical childhoods both locally and globally. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:54:54', '2021-08-04T09:58:37');
