INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100391, 'PsycINFO (en)', 'PsycINFO (sv)', 'PsycINFO provides access to international literature in psychology and related disciplines. The database is enriched with literature from an array of disciplines related to psychology such as psychiatry, education, business, medicine, nursing, pharmacology, law, linguistics, and social work. All records from 1967 to the present are indexed using the Thesaurus of Psychological Index Terms. The sources include professional journals, chapters, books, reports, theses and dissertations, published internationally. (en)', 'PsycINFO provides access to international literature in psychology and related disciplines. The database is enriched with literature from an array of disciplines related to psychology such as psychiatry, education, business, medicine, nursing, pharmacology, law, linguistics, and social work. All records from 1967 to the present are indexed using the Thesaurus of Psychological Index Terms. The sources include professional journals, chapters, books, reports, theses and dissertations, published internationally. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:55:10', '2022-06-08T14:42:07');
