INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100397, 'Recensionssök 1920-1966 (en)', 'Recensionssök 1920-1966 (sv)', 'Recensionssök 1920-1966 är ett elektroniskt index över recensioner av svensk och utländsk litteratur införda i svensk press under den angivna tidsperioden. Recensioner av skönlitteratur utgör den största delen av materialet. Till viss del ingår också litteraturkritik och litteraturhistoria, allmän historia, biografi, filosofi m.m.     Databasen fyller en lucka mellan andra tryckta eller elektroniskt tillgängliga artikelindex. Den omfattar också till skillnad från dessa i princip hela dagspressen jämte vissa tidskrifter. Sammantaget ingår ca 250 olika publikationer i registret. (en)', 'Recensionssök 1920-1966 är ett elektroniskt index över recensioner av svensk och utländsk litteratur införda i svensk press under den angivna tidsperioden. Recensioner av skönlitteratur utgör den största delen av materialet. Till viss del ingår också litteraturkritik och litteraturhistoria, allmän historia, biografi, filosofi m.m.     Databasen fyller en lucka mellan andra tryckta eller elektroniskt tillgängliga artikelindex. Den omfattar också till skillnad från dessa i princip hela dagspressen jämte vissa tidskrifter. Sammantaget ingår ca 250 olika publikationer i registret. (sv)', FALSE, '', '', FALSE, TRUE, 'freely_available', FALSE, '2016-02-19T10:55:10', '2020-07-07T14:07:36');
