INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100433, 'Socialstyrelsens författningssamling (en)', 'Socialstyrelsens författningssamling (sv)', 'I Socialstyrelsens författningssamling (SOSFS) publiceras föreskrifter och allmänna råd samt kungörelser. Föreskrifter är bindande regler. Allmänna råd innehåller rekommendationer om hur en författning kan eller bör tillämpas och utesluter inte andra sätt att uppnå de mål som avses i författningen. Kungörelser är oftast upphävanden av allmänna råd. I databasen presenteras grunddokumenten tillsammans med eventuella ändringar. Det innebär att om man till exempel söker på SOSFS 1994:20 visar resultatet av sökningen även SOSFS 1998:12 och 1999:23 eftersom dessa två föreskrifter är ändringar av grundförfattningen. (en)', 'I Socialstyrelsens författningssamling (SOSFS) publiceras föreskrifter och allmänna råd samt kungörelser. Föreskrifter är bindande regler. Allmänna råd innehåller rekommendationer om hur en författning kan eller bör tillämpas och utesluter inte andra sätt att uppnå de mål som avses i författningen. Kungörelser är oftast upphävanden av allmänna råd. I databasen presenteras grunddokumenten tillsammans med eventuella ändringar. Det innebär att om man till exempel söker på SOSFS 1994:20 visar resultatet av sökningen även SOSFS 1998:12 och 1999:23 eftersom dessa två föreskrifter är ändringar av grundförfattningen. (sv)', FALSE, '', '', FALSE, TRUE, 'freely_available', FALSE, '2016-02-19T10:55:10', '2022-09-05T09:01:24');
