INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100445, 'SveMed+ (en)', 'SveMed+ (sv)', 'SveMed+ är en bibliografisk databas med referenser till artiklar inom det medicinska området. En del av materialet är av mer populärvetenskaplig karaktär. Förutom biomedicin bevakas patientinformation och icke kliniska aspekter av hälso- och sjukvård. (en)', 'SveMed+ är en bibliografisk databas med referenser till artiklar inom det medicinska området. En del av materialet är av mer populärvetenskaplig karaktär. Förutom biomedicin bevakas patientinformation och icke kliniska aspekter av hälso- och sjukvård. (sv)', FALSE, '', '', FALSE, TRUE, 'freely_available', FALSE, '2016-02-19T10:55:18', '2019-05-24T12:29:27');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100448, 'Svensk filmdatabas (en)', 'Svensk filmdatabas (sv)', 'Upplysningar om samtliga svenska långfilmer sedan 1897, och dessutom en stor mängd svenska kort- , dokumentär- och tv-filmer.    Det går att söka på t ex filmtitel, premiärår, roll och vilket bolag som spelade in filmen. Skådespelare och filmarbetare är också sökbara.   Filmfaktablad i PDF-format. (en)', 'Upplysningar om samtliga svenska långfilmer sedan 1897, och dessutom en stor mängd svenska kort- , dokumentär- och tv-filmer.    Det går att söka på t ex filmtitel, premiärår, roll och vilket bolag som spelade in filmen. Skådespelare och filmarbetare är också sökbara.   Filmfaktablad i PDF-format. (sv)', FALSE, '', '', FALSE, TRUE, 'freely_available', FALSE, '2016-02-19T10:55:18', '2019-05-24T12:28:27');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100450, 'Svensk kulturväxtdatabas (en)', 'Svensk kulturväxtdatabas (sv)', 'Svensk Kulturväxtdatabas (SKUD) är en nomenklaturdatabas och svensk namnstandard för kulturväxter, som utvecklats av IT-experter vid SLU och botanister i Göteborgs botaniska trädgård och Evolutionsmuseet, Uppsala universitet.    Avsikten med databasen är att skapa ett lättillgängligt referensverktyg och en likriktare i Sverige för att underlätta kommunikation om kulturväxter – både bland professionella och lekmän.    Den innehåller även uppgifter om växternas naturliga utbredning, användning och livsform. Databasen har byggts med potential för att kunna utvecklas till en nordisk namndatabas.    Den vänder sig till verk, myndigheter, företag, forskare, skribenter, konsumenter och andra som arbetar med eller använder kulturväxter.
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100451, 'Svensk medicinalhistorisk bibliografi (Medorg) (en)', 'Svensk medicinalhistorisk bibliografi (Medorg) (sv)', 'Svensk medicinalhistorisk bibliografi innehåller totalt ca 50 000 referenser till artiklar, utredningar, betänkanden med mera inom området svenskt medicinalväsen. Den omfattar perioden 1663-1967 och är producerad i samarbete mellan Socialstyrelsen och Statistiska centralbyrån. (en)', 'Svensk medicinalhistorisk bibliografi innehåller totalt ca 50 000 referenser till artiklar, utredningar, betänkanden med mera inom området svenskt medicinalväsen. Den omfattar perioden 1663-1967 och är producerad i samarbete mellan Socialstyrelsen och Statistiska centralbyrån. (sv)', FALSE, '', '', FALSE, TRUE, 'freely_available', FALSE, '2016-02-19T10:55:18', '2019-05-24T12:24:31');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100452, 'Svensk mediedatabas (en)', 'Svensk mediedatabas (sv)', 'Svensk mediedatabas, SMDB, är söktjänsten för Kungliga bibliotekets audiovisuella samlingar. Här hittar du uppgifter om tv, radio, video, biovisad film, skivor och multimedier.    SMDB täcker de flesta svenska sändningar och utgåvor sedan 1979, men innehåller även äldre material. Bland annat finns en nästan fullständig förteckning över svenska skivor från slutet av 1800-talet och framåt. KB arbetar löpande med att komplettera databasen både med historiskt och dagsaktuellt innehåll. (en)', 'Svensk mediedatabas, SMDB, är söktjänsten för Kungliga bibliotekets audiovisuella samlingar. Här hittar du uppgifter om tv, radio, video, biovisad film, skivor och multimedier.    SMDB täcker de flesta svenska sändningar och utgåvor sedan 1979, men innehåller även äldre material. Bland annat finns en nästan fullständig förteckning över svenska skivor från slutet av 1800-talet och framåt. KB arbetar löpande med att komplettera databasen både med historiskt och dagsaktuellt innehåll. (sv)', FALSE, '', '', FALSE, TRUE, 'freely_available', FALSE, '2016-02-19T10:55:18', '2019-05-24T12:25:19');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100453, 'Svensk MeSH (en)', 'Svensk MeSH (sv)', 'MeSH innehåller de kontrollerade termer som används för indexering av artiklar i Medline/PubMed. Originaldata från NLM har kompletterats med svenska översättningar utförda av personal vid Karolinska Institutets Bibliotek. Svensk MeSH översätter från svenska till engelska, och från engelska till svenska. (en)', 'MeSH innehåller de kontrollerade termer som används för indexering av artiklar i Medline/PubMed. Originaldata från NLM har kompletterats med svenska översättningar utförda av personal vid Karolinska Institutets Bibliotek. Svensk MeSH översätter från svenska till engelska, och från engelska till svenska. (sv)', FALSE, '', '', FALSE, TRUE, 'freely_available', FALSE, '2016-02-19T10:55:18', '2021-12-10T10:15:39');
