INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100455, 'Svensk patentdatabas (en)', 'Svensk patentdatabas (sv)', 'PRV:s e-tjänst Svensk Patentdatabas är en databas som innehåller svenska patent, svenska offentliga patentansökningar, europeiska patent (EP-patent) som validerats i Sverige, EP-ansökningar som publicerats under §88 PL (Patentlagen), beviljade EP-patent där Sverige är designerat (sökbara i databasen i sex månader från EPO:s beviljandedatum), tilläggsskydd och förlängd tilläggsskyddstid för patent. Patentdatabasen omfattar de uppgifter och dokument från 1885 fram till nu som finns i PRV:s arkiv. (en)', 'PRV:s e-tjänst Svensk Patentdatabas är en databas som innehåller svenska patent, svenska offentliga patentansökningar, europeiska patent (EP-patent) som validerats i Sverige, EP-ansökningar som publicerats under §88 PL (Patentlagen), beviljade EP-patent där Sverige är designerat (sökbara i databasen i sex månader från EPO:s beviljandedatum), tilläggsskydd och förlängd tilläggsskyddstid för patent. Patentdatabasen omfattar de uppgifter och dokument från 1885 fram till nu som finns i PRV:s arkiv. (sv)', FALSE, '', '', FALSE, TRUE, 'freely_available', FALSE, '2016-02-19T10:55:18', '2019-05-24T11:53:45');
