INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100465, 'Svenskt diplomatariums huvudkartotek över medeltidsbreven (en)', 'Svenskt diplomatariums huvudkartotek över medeltidsbreven (sv)', 'Riksarkivets avdelning Svenskt Diplomatarium publicerar våra bevarade medeltidsdokument (diplom). Det rör sig om såväl originalbrev på pergament och papper som medeltida och eftermedeltida avskrifter. Språket i texterna är oftast på latin eller fornsvenska men ibland förekommer medellågtyska. Huvudkartoteket omfattar uppgifter om över 40.000 svenska brev eller brev rörande Sverige och medger en rad olika sökmöjligheter, bl.a. på personnamn, ortnamn, datum och viktiga sakuppgifter. I kartoteket finner man dessutom alltid utförliga upplysningar om brevens tradering, d.v.s. hur och i vilken form de har bevarats, och i många fall hänvisningar till litteratur. (en)', 'Riksarkivets avdelning Svenskt Diplomatarium publicerar våra bevarade medeltidsdokument (diplom). Det rör sig om såväl originalbrev på pergament och papper som medeltida och eftermedeltida avskrifter. Språket i texterna är oftast på latin eller fornsvenska men ibland förekommer medellågtyska. Huvudkartoteket omfattar uppgifter om över 40.000 svenska brev eller brev rörande Sverige och medger en rad olika sökmöjligheter, bl.a. på personnamn, ortnamn, datum och viktiga sakuppgifter. I kartoteket finner man dessutom alltid utförliga upplysningar om brevens tradering, d.v.s. hur och i vilken form de har bevarats, och i många fall hänvisningar till litteratur. (sv)', FALSE, '', '', FALSE, TRUE, 'freely_available', FALSE, '2016-02-19T10:55:18', '2019-05-24T11:41:51');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100466, 'Svenskt översättarlexikon (en)', 'Svenskt översättarlexikon (sv)', 'Svenskt översättarlexikon är ett biobibliografiskt uppslagsverk med artiklar om svenska och finlandssvenska översättare från äldsta tid till idag. Databasen har utvecklats på Södertörns högskolebibliotek under medverkan av litteraturvetare, språkhistoriker, översättare och kritiker. (en)', 'Svenskt översättarlexikon är ett biobibliografiskt uppslagsverk med artiklar om svenska och finlandssvenska översättare från äldsta tid till idag. Databasen har utvecklats på Södertörns högskolebibliotek under medverkan av litteraturvetare, språkhistoriker, översättare och kritiker. (sv)', FALSE, '', '', FALSE, TRUE, 'freely_available', FALSE, '2016-02-19T10:55:18', '2019-05-24T11:36:40');
