INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100468, 'SwePub (en)', 'SwePub (sv)', 'SwePub ger dig möjlighet att söka bland artiklar, konferensbidrag, avhandlingar m.m. som publicerats vid svenska lärosäten. Använd SwePub för att få en översikt av vad som skrivits inom ditt forskningsområde, få tag på fulltext, abstract och citeringar. (en)', 'SwePub ger dig möjlighet att söka bland artiklar, konferensbidrag, avhandlingar m.m. som publicerats vid svenska lärosäten. Använd SwePub för att få en översikt av vad som skrivits inom ditt forskningsområde, få tag på fulltext, abstract och citeringar. (sv)', FALSE, '', '', FALSE, TRUE, 'freely_available', FALSE, '2016-02-19T10:55:18', '2019-05-24T11:24:18');
