INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100470, 'Taylor & Francis online (en)', 'Taylor & Francis online (sv)', 'Access to full text e-journals (including backfiles often from the first issue of the first volume) subscribed to by the Gothenburg University Library and published by the Taylor and Francis Group, including Routledge and Psychology Press. You can browse journal titles alphabetically or by subject, or search the contents of over 1000 journals. Journals from which you will have access to fulltext articles are indicated by a green square or, if only some articles are available, an orange square. To limit your search to full text articles, on the Advanced Search page check the box My subscriptions only. (en)', 'Access to full text e-journals (including backfiles often from the first issue of the first volume) subscribed to by the Gothenburg University Library and published by the Taylor and Francis Group, including Routledge and Psychology Press. You can browse journal titles alphabetically or by subject, or search the contents of over 1000 journals. Journals from which you will have access to fulltext articles are indicated by a green square or, if only some articles are available, an orange square. To limit your search to full text articles, on the Advanced Search page check the box My subscriptions only. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:55:18', '2022-01-05T12:43:24');
