INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100473, 'The Digital Karl Barth Library (en)', 'The Digital Karl Barth Library (sv)', 'This library is a database of the works of Swiss theologian Karl Barth, 1886-1968, one of the 20th century''s most influential Christian theologians. The database includes critical editions of Barth''s German works ("Gesamtausgabe") along with his magnum opus, the 14 volume "Kirchliche Dogmatik" or "Church Dogmatics." Also included are sermons, lectures, other academic writings and correspondence. (en)', 'This library is a database of the works of Swiss theologian Karl Barth, 1886-1968, one of the 20th century''s most influential Christian theologians. The database includes critical editions of Barth''s German works ("Gesamtausgabe") along with his magnum opus, the 14 volume "Kirchliche Dogmatik" or "Church Dogmatics." Also included are sermons, lectures, other academic writings and correspondence. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:55:18', '2022-04-12T15:33:18');
