INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100476, 'Thesaurus Linguae Latinae (en)', 'Thesaurus Linguae Latinae (sv)', 'Thesaurus Linguae Latinae is the largest Latin dictionary in the world and the first to cover all the Latin texts from the classical period up to about 600 A.D. The database contains all content that is also available in print.
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100477, 'Thieme MedOne Education (en)', 'Thieme MedOne Education (sv)', 'The Thieme MedOne Education is an online reference collection, including the Flexibook Atlases and Textbooks Series. Here you will find medical and scientific books as well as a growing number of books in alternative medicine. (en)', 'The Thieme MedOne Education is an online reference collection, including the Flexibook Atlases and Textbooks Series. Here you will find medical and scientific books as well as a growing number of books in alternative medicine. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:55:18', '2022-09-16T15:16:38');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100478, 'Thieme teaching assistant (en)', 'Thieme teaching assistant (sv)', 'The Thieme Teaching Assistant is a presentation tool featuring clinical images from the new Gilroy et al. single-volume Atlas of Anatomy. The Teaching Assistant platform enables instructors to enhance students'' learning experiences by incorporating dynamic visuals into lectures and course materials. (en)', 'The Thieme Teaching Assistant is a presentation tool featuring clinical images from the new Gilroy et al. single-volume Atlas of Anatomy. The Teaching Assistant platform enables instructors to enhance students'' learning experiences by incorporating dynamic visuals into lectures and course materials. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:55:18', '2022-09-16T15:17:13');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100479, 'The Times Digital Archive (en)', 'The Times Digital Archive (sv)', 'The Times Digital Archive is a full-image online archive of every page published by The Times 1785-1985. The entire newspaper is captured, with all articles, advertisements and illustrations/photos divided into categories to facilitate searching.
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100480, 'The Times Literary Supplement Historical Archive (en)', 'The Times Literary Supplement Historical Archive (sv)', 'The Times Literary Supplement Historical Archive contains a complete online facsimile edition from its first issue in 1902 to 2014. Users can access any page through full-text searching or browse issues by date, book title, author, contributor, illustrator, editor and translator. Searches by article type include book reviews, theatre reviews, leading articles, letters, poems and obituaries.
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100481, 'TradeLawGuide (en)', 'TradeLawGuide (sv)', 'TradeLawGuide (TLG) is an innovative tool for researching World Trade Organization (WTO) law. The database provides access to WTO jurisprudence (reports, awards and decisions); ascertains the status of passages in WTO jurisprudence and “notes up” passages in a methodical and efficient manner; reviews provisions and jurisprudence encompassing the rules of treaty interpretation applicable to the WTO agreements and instruments; and examines terms and phrases defined in the texts of the WTO agreements and instruments or elaborated upon in statements in WTO jurisprudence. (en)', 'TradeLawGuide (TLG) is an innovative tool for researching World Trade Organization (WTO) law. The database provides access to WTO jurisprudence (reports, awards and decisions); ascertains the status of passages in WTO jurisprudence and “notes up” passages in a methodical and efficient manner; reviews provisions and jurisprudence encompassing the rules of treaty interpretation applicable to the WTO agreements and instruments; and examines terms and phrases defined in the texts of the WTO agreements and instruments or elaborated upon in statements in WTO jurisprudence. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:55:18', '2022-03-07T15:29:17');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100482, 'Translated texts for historians E-Library (en)', 'Translated texts for historians E-Library (sv)', '300–800 AD is the time of late antiquity and the early middle ages: the transformation of the classical world, the beginnings of Europe and of Islam, and the evolution of Byzantium. The Translated Texts for Historians book series makes available historical sources from A.D. 300–800 translated into English, in many cases for the first time. The Translated Texts for Historians E-Library offers this collection as a digital resource. The texts have been translated into English from Greek, Latin, Syriac, Coptic, Arabic, Georgian, Armenian and Old Irish. The geographic range covers Syria, Arabia, Armenia, Georgia and Egypt in the East; North Africa; major cities of the Roman Empire (Antioch, Alexandria and Constantinople); and Spain, Gaul, Italy, Britain and Ireland in the West.
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100484, 'TRID (en)', 'TRID (sv)', 'TRID contains records of transportation research worldwide. It combines the records from TRB''s Transportation Research Information Services (TRIS) Database and the OECD''s Joint Transport Research Centre’s International Transport Research Documentation (ITRD) Database. (en)', 'TRID contains records of transportation research worldwide. It combines the records from TRB''s Transportation Research Information Services (TRIS) Database and the OECD''s Joint Transport Research Centre’s International Transport Research Documentation (ITRD) Database. (sv)', FALSE, '', '', FALSE, TRUE, 'freely_available', FALSE, '2016-02-19T10:55:18', '2019-05-23T13:32:52');
