INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100495, 'UNECE statistical database (en)', 'UNECE statistical database (sv)', 'The UNECE Statistical Database contains a wide range of data covering Europe, North America and Central Asia. Users can download data free of charge via English or Russian interfaces. This database is maintained by the Statistical Division of the UNECE Secretariat. It provides free access to data structured to facilitate easy retrieval of statistics by users. It is organised by subject or policy areas. Multi-dimensional tables present data by country, by various socio-economic classifications related to the context, and by time period. Data is presented under three subject areas: macroeconomic, gender, and transportation statistics. (en)', 'The UNECE Statistical Database contains a wide range of data covering Europe, North America and Central Asia. Users can download data free of charge via English or Russian interfaces. This database is maintained by the Statistical Division of the UNECE Secretariat. It provides free access to data structured to facilitate easy retrieval of statistics by users. It is organised by subject or policy areas. Multi-dimensional tables present data by country, by various socio-economic classifications related to the context, and by time period. Data is presented under three subject areas: macroeconomic, gender, and transportation statistics. (sv)', FALSE, '', '', FALSE, TRUE, 'freely_available', FALSE, '2016-02-19T10:55:27', '2019-05-22T14:51:25');
