INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100499, 'Oxford bibliographies: Latin American studies (en)', 'Oxford bibliographies: Latin American studies (sv)', 'Latin American studies includes a vast range of disciplinary perspectives, including history, sociology, economics, anthropology, and political science. Oxford Bibliographies in Latin American studies is an entirely new and unique type of reference tool that has been specially created to meet a great need among today’s students, scholars, and researchers. It offers more than other bibliography initiatives on- and offline by providing expert commentary to help users find, negotiate, and assess the large amount of information readily available to them. (en)', 'Latin American studies includes a vast range of disciplinary perspectives, including history, sociology, economics, anthropology, and political science. Oxford Bibliographies in Latin American studies is an entirely new and unique type of reference tool that has been specially created to meet a great need among today’s students, scholars, and researchers. It offers more than other bibliography initiatives on- and offline by providing expert commentary to help users find, negotiate, and assess the large amount of information readily available to them. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:55:27', '2021-08-04T14:17:43');
