INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(100503, 'Westlaw (en)', 'Westlaw (sv)', 'Combining materials from several content providers Westlaw International offers a wide collection of legal and regulatory information dealing with all aspects of law. It covers most areas of the world, also international organizations such as EU, United Nations, WTO and international tribunals. The texts include case law, legislation, law reviews, treaties, directories, journal articles, commentaries and other texts, full text or summaries. (en)', 'Combining materials from several content providers Westlaw International offers a wide collection of legal and regulatory information dealing with all aspects of law. It covers most areas of the world, also international organizations such as EU, United Nations, WTO and international tribunals. The texts include case law, legislation, law reviews, treaties, directories, journal articles, commentaries and other texts, full text or summaries. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2016-02-19T10:55:27', '2022-03-07T14:11:20');
