INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(164762, 'Svensk historisk bibliografi (en)', 'Svensk historisk bibliografi (sv)', 'Svensk historisk bibliografi-digital (SHBd) är en ämnesbibliografi över svensk historia. Här finns hänvisningar till monografier, avhandlingar, uppsatser, recensioner, artiklar i dagstidningar och annat material som rör Sveriges historia. Bibliografin omfattar publiceringsåren 1771-2010 och sammanlagt ingår över 176 000 poster. SHBd omfattar alla arbeten utgivna mellan 1771 och 2010 som förtecknats i Svensk historisk bibliografi. Svensk historisk bibliografi uppdateras i princip inte efter 2010, men det kan förekomma viss påfyllning av monografiska poster som registrerats i Sveriges nationalbibliografi. (en)', 'Svensk historisk bibliografi-digital (SHBd) är en ämnesbibliografi över svensk historia. Här finns hänvisningar till monografier, avhandlingar, uppsatser, recensioner, artiklar i dagstidningar och annat material som rör Sveriges historia. Bibliografin omfattar publiceringsåren 1771-2010 och sammanlagt ingår över 176 000 poster. SHBd omfattar alla arbeten utgivna mellan 1771 och 2010 som förtecknats i Svensk historisk bibliografi. Svensk historisk bibliografi uppdateras i princip inte efter 2010, men det kan förekomma viss påfyllning av monografiska poster som registrerats i Sveriges nationalbibliografi. (sv)', FALSE, '', '', FALSE, TRUE, 'freely_available', FALSE, '2018-06-12T08:00:02', '2019-05-24T12:27:21');
