INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(188062, 'Abbreviationes online (en)', 'Abbreviationes online (sv)', 'Abbreviationes identifies abbreviations used in medieval Latin manuscripts ("Latin paleography"). The manuscripts come from "libraries throughout Europe - from Catania in the South, Uppsala in the North, Coimbra in the West to St. Petersburg in the East. It includes large collections such as the manuscripts held by the Vatican Library or the libraries at Oxford and Paris as well as many smaller collections. Major collections in the United States, such as the manuscripts held by the Morgan Library (New York City, New York) and the Huntington Library (San Marino, California), are also included." The database currently comprises over 70,000 entries containing a total of 80,098 references to manuscripts. Entries in the database cover the 8th century through the 15th century. (en)', 'Abbreviationes identifies abbreviations used in medieval Latin manuscripts ("Latin paleography"). The manuscripts come from "libraries throughout Europe - from Catania in the South, Uppsala in the North, Coimbra in the West to St. Petersburg in the East. It includes large collections such as the manuscripts held by the Vatican Library or the libraries at Oxford and Paris as well as many smaller collections. Major collections in the United States, such as the manuscripts held by the Morgan Library (New York City, New York) and the Huntington Library (San Marino, California), are also included." The database currently comprises over 70,000 entries containing a total of 80,098 references to manuscripts. Entries in the database cover the 8th century through the 15th century. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2019-06-03T11:10:53', '2021-08-05T12:43:25');
