INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(188273, 'Dictionary of Old English: A to I  (en)', 'Dictionary of Old English: A to I  (sv)', 'The Dictionary of Old English (DOE) is a dictionary of the Old English language, published by the Centre for Medieval Studies, University of Toronto, Canada. The dictionary defines the vocabulary of the first six centuries (600-1150 A.D.) of the English language. The DOE complements the Middle English Dictionary (which covers the period 1100-1500 A.D.) and the Oxford English Dictionary, the three together providing a full description of the vocabulary of English. 
