INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(188846, 'POWO Plants of the World Online (en)', 'POWO Plants of the World Online (sv)', 'An international collaborative programme that has as a primary aim to make available digitized data of the world’s flora gathered from the past 250 years of botanical exploration and research. POWO also aims to make freely available electronic data created by different projects but that no longer have an online presence or where data was never made available externally. It delivers information on the taxonomy, identification, images, distribution, traits, threat status, molecular phylogenies and uses of vascular plants worldwide. The data are sourced from the Royal Botanic Gardens, Kew as well as its partners and collaborators who generously contribute data and make it openly accessible on POWO. (en)', 'An international collaborative programme that has as a primary aim to make available digitized data of the world’s flora gathered from the past 250 years of botanical exploration and research. POWO also aims to make freely available electronic data created by different projects but that no longer have an online presence or where data was never made available externally. It delivers information on the taxonomy, identification, images, distribution, traits, threat status, molecular phylogenies and uses of vascular plants worldwide. The data are sourced from the Royal Botanic Gardens, Kew as well as its partners and collaborators who generously contribute data and make it openly accessible on POWO. (sv)', FALSE, '', '', FALSE, TRUE, 'freely_available', FALSE, '2021-09-07T14:18:13', '2021-09-07T14:18:13');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(188872, 'Le Bon Usage (en)', 'Le Bon Usage (sv)', 'Le Bon Usage, informally called Le Grevisse, is a descriptive book about French grammar. It describes the usage of the French language, primarily in its written literary form. It includes numerous examples and counter-examples taken from Francophone literature of various periods, including newspapers, to form a reference for teachers of French, and in particular, authors and editors.
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(188874, 'Integrum Profi (en)', 'Integrum Profi (sv)', 'Integrum Profi is the world''s largest archive of mass media of Russia, the CIS, and the Baltic states. The archive contains Moscow and Russian regional newspapers and journals, business and law databases, encyclopedias and dictionaries. Date coverage is dependent on publication but extends from the early 1990s to current. Search interface available in English and Russian.
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(188884, 'Etymological Dictionary of the Armenian Inherited Lexicon (en)', 'Etymological Dictionary of the Armenian Inherited Lexicon (sv)', 'Etymological Dictionary of the Armenian Inherited Lexicon provides an up-to-date description of the Indo-European lexical stock of Armenian with systematic inclusion of dialectal data. It incorporates the lexical, phonetic, and morphological material in the Armenian dialects into the etymological treatment of the Indo-European lexicon (en)', 'Etymological Dictionary of the Armenian Inherited Lexicon provides an up-to-date description of the Indo-European lexical stock of Armenian with systematic inclusion of dialectal data. It incorporates the lexical, phonetic, and morphological material in the Armenian dialects into the etymological treatment of the Indo-European lexicon (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2021-09-30T09:49:42', '2022-02-10T11:33:31');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(188885, 'Etymological Dictionary of the Baltic Inherited Lexicon (en)', 'Etymological Dictionary of the Baltic Inherited Lexicon (sv)', 'Etymological Dictionary of the Baltic Inherited Lexicon aims at combining recent insights from comparative Indo-European linguistics with modern Balto-Slavic accentology. (en)', 'Etymological Dictionary of the Baltic Inherited Lexicon aims at combining recent insights from comparative Indo-European linguistics with modern Balto-Slavic accentology. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2021-09-30T10:04:29', '2022-02-10T11:34:08');
