INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(188887, 'Old Frisian Etymological Dictionary (en)', 'Old Frisian Etymological Dictionary (sv)', 'Old Frisian Etymological Dictionary is based on the lexicon of Riustring 1 manuscript, and the latest insights of Comparative Indo-European Linguistics have been systematically incorporated. The entries are provided with analysis of Old Frisian dialectal forms, with Proto-Frisian reconstructions, and with Germanic and Indo- European cognates. (en)', 'Old Frisian Etymological Dictionary is based on the lexicon of Riustring 1 manuscript, and the latest insights of Comparative Indo-European Linguistics have been systematically incorporated. The entries are provided with analysis of Old Frisian dialectal forms, with Proto-Frisian reconstructions, and with Germanic and Indo- European cognates. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2021-09-30T10:21:01', '2022-02-10T11:20:51');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(188888, 'Etymological Dictionary of Proto-Germanic (en)', 'Etymological Dictionary of Proto-Germanic (sv)', 'Etymological Dictionary of Proto-Germanic offers material collected from old and new Germanic sources, ranging from Gothic to Elfdalian, from Old English to the Swiss dialects, and incorporates several advances in Proto-Germanic phonology, morphology and derivation. With its approximately 2,800 headwords and at least as many derivations, it covers the larger part of the Proto-Germanic vocabulary, and attempts to trace it back to its Proto-Indo-European foundations.  (en)', 'Etymological Dictionary of Proto-Germanic offers material collected from old and new Germanic sources, ranging from Gothic to Elfdalian, from Old English to the Swiss dialects, and incorporates several advances in Proto-Germanic phonology, morphology and derivation. With its approximately 2,800 headwords and at least as many derivations, it covers the larger part of the Proto-Germanic vocabulary, and attempts to trace it back to its Proto-Indo-European foundations.  (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2021-09-30T10:29:17', '2022-02-10T11:31:35');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(188889, 'Etymological Dictionary of Greek (en)', 'Etymological Dictionary of Greek (sv)', 'Etymological Dictionary of Greek covers 2000 years of Ancient Greek: from Mycenaean via Homer and the classical period to lexicographers, such as Hesychius (5th century A.D.) It consists of 7500 entries with revised etymologies. Each entry gives information about the origin of the Greek word and its first date of attestation. It further provides all etymologically relevant variants, dialectal forms, derivatives, compounds, and bibliographical references. (en)', 'Etymological Dictionary of Greek covers 2000 years of Ancient Greek: from Mycenaean via Homer and the classical period to lexicographers, such as Hesychius (5th century A.D.) It consists of 7500 entries with revised etymologies. Each entry gives information about the origin of the Greek word and its first date of attestation. It further provides all etymologically relevant variants, dialectal forms, derivatives, compounds, and bibliographical references. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2021-09-30T10:33:02', '2022-02-10T11:32:07');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(188890, 'Etymological Dictionary of the Hittite Inherited Lexicon (en)', 'Etymological Dictionary of the Hittite Inherited Lexicon (sv)', 'Etymological Dictionary of the Hittite Inherited Lexicon is an etymological dictionary of the entire Hittite lexicon of Indo-European origin in which all words are treated in a coherent way. Furthermore, it provides a thorough description of the synchronic phonological system of Hittite as well as a comprehensive study of the Hittite historical morphology and phonology.  (en)', 'Etymological Dictionary of the Hittite Inherited Lexicon is an etymological dictionary of the entire Hittite lexicon of Indo-European origin in which all words are treated in a coherent way. Furthermore, it provides a thorough description of the synchronic phonological system of Hittite as well as a comprehensive study of the Hittite historical morphology and phonology.  (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2021-09-30T10:37:12', '2022-02-10T11:28:50');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(188891, 'Etymological Dictionary of the Iranian Verb (en)', 'Etymological Dictionary of the Iranian Verb (sv)', 'Etymological Dictionary of the Iranian Verb provides a critical survey of all the verbs that may have existed in Proto-Iranian as deduced from the attested Iranian descendants and their archaic sister language, Sanskrit. This is accompanied by an analysis of the morphology and assessment of the provenance.  (en)', 'Etymological Dictionary of the Iranian Verb provides a critical survey of all the verbs that may have existed in Proto-Iranian as deduced from the attested Iranian descendants and their archaic sister language, Sanskrit. This is accompanied by an analysis of the morphology and assessment of the provenance.  (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2021-09-30T10:40:29', '2022-02-10T11:31:01');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(188892, 'Etymological Dictionary of Latin and the other Italic Languages (en)', 'Etymological Dictionary of Latin and the other Italic Languages (sv)', 'Etymological Dictionary of Latin and the other Italic Languages covers the entire Latin lexicon of Indo-European origin. It consists of nearly 1900 entries, which altogether discuss about 8000 Latin lemmata. All words attested before Cicero are included, together with their first date of attestation in Latin. The dictionary also includes all the inherited words found in the other ancient Italic languages, such as Oscan, Umbrian and South Picene; thus, it also serves as an etymological dictionary of Italic. (en)', 'Etymological Dictionary of Latin and the other Italic Languages covers the entire Latin lexicon of Indo-European origin. It consists of nearly 1900 entries, which altogether discuss about 8000 Latin lemmata. All words attested before Cicero are included, together with their first date of attestation in Latin. The dictionary also includes all the inherited words found in the other ancient Italic languages, such as Oscan, Umbrian and South Picene; thus, it also serves as an etymological dictionary of Italic. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2021-09-30T10:43:23', '2022-02-10T11:26:49');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(188893, 'Cuneiform Luvian Lexicon (en)', 'Cuneiform Luvian Lexicon (sv)', 'Cuneiform Luvian Lexicon aims to provide a provisional index, as exhaustive as possible, of all attested Cuneiform Luvian lexemes. It is intended to be complete for the CLuvian corpus as established by Frank Starke, Die keilschrift-luwischen Texte in Umschrift (StBoT 30), Wiesbaden: 1985 (with the addenda and corrigenda in StBoT 31 (1990) 592-607). (en)', 'Cuneiform Luvian Lexicon aims to provide a provisional index, as exhaustive as possible, of all attested Cuneiform Luvian lexemes. It is intended to be complete for the CLuvian corpus as established by Frank Starke, Die keilschrift-luwischen Texte in Umschrift (StBoT 30), Wiesbaden: 1985 (with the addenda and corrigenda in StBoT 31 (1990) 592-607). (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2021-09-30T10:46:41', '2022-02-10T11:24:21');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(188894, 'Etymological Dictionary of Proto-Nostratic (en)', 'Etymological Dictionary of Proto-Nostratic (sv)', 'Etymological Dictionary of Proto-Nostratic is a comprehensive comparison of Proto-Indo-European, in all its stages of development and in all its aspects, with various other language families of Northern Eurasia, the Middle East, and the Indian subcontinent.  All aspects of the putative proto-language are discussed in detail: phonology, morphology, vocabulary, syntax, and homelands. (en)', 'Etymological Dictionary of Proto-Nostratic is a comprehensive comparison of Proto-Indo-European, in all its stages of development and in all its aspects, with various other language families of Northern Eurasia, the Middle East, and the Indian subcontinent.  All aspects of the putative proto-language are discussed in detail: phonology, morphology, vocabulary, syntax, and homelands. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2021-09-30T10:51:06', '2022-02-10T11:27:55');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(188895, 'Etymological Dictionary of the Slavic Inherited Lexicon (en)', 'Etymological Dictionary of the Slavic Inherited Lexicon (sv)', 'Etymological Dictionary of the Slavic Inherited Lexicon combines recent insights from the field of comparative Indo-European linguistics with modern Balto-Slavic accentology. The entries of the dictionary are alphabetically arranged Proto-Slavic etyma. Each lemma consists of a number of fields which contain the evidence, reconstructions and notes.  (en)', 'Etymological Dictionary of the Slavic Inherited Lexicon combines recent insights from the field of comparative Indo-European linguistics with modern Balto-Slavic accentology. The entries of the dictionary are alphabetically arranged Proto-Slavic etyma. Each lemma consists of a number of fields which contain the evidence, reconstructions and notes.  (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2021-09-30T10:57:23', '2022-02-10T11:29:31');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(188896, 'Etymological Dictionary of Tocharian B (en)', 'Etymological Dictionary of Tocharian B (sv)', 'Etymological Dictionary of Tocharian B includes substantially all Tocharian B words found in regularly published texts. All words except proper names are provided with example contexts. Each word is given in all its various attested morphological forms, in its variant spellings, and discussed semantically, syntactically (where appropriate), and etymologically.  (en)', 'Etymological Dictionary of Tocharian B includes substantially all Tocharian B words found in regularly published texts. All words except proper names are provided with example contexts. Each word is given in all its various attested morphological forms, in its variant spellings, and discussed semantically, syntactically (where appropriate), and etymologically.  (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2021-09-30T11:28:34', '2022-02-10T11:30:13');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(188897, 'Materials for an Etymological Database of North Lechitic Dialects (en)', 'Materials for an Etymological Database of North Lechitic Dialects (sv)', 'Materials for an Etymological Database of North Lechitic Dialects intends to present a systematic overview of inherited lexical material from several dialects of Slavic Pomerania, most importantly, Slovincian and Kashubian. Particular attention is given to paradigmatic and phonemic features that reflect the Proto-Slavic accent system. Structured as a traditional etymological dictionary, it combines comparative data from other Slavic languages with known accentological reconstructions drawn from several studies of other dialects and medieval Slavic texts. (en)', 'Materials for an Etymological Database of North Lechitic Dialects intends to present a systematic overview of inherited lexical material from several dialects of Slavic Pomerania, most importantly, Slovincian and Kashubian. Particular attention is given to paradigmatic and phonemic features that reflect the Proto-Slavic accent system. Structured as a traditional etymological dictionary, it combines comparative data from other Slavic languages with known accentological reconstructions drawn from several studies of other dialects and medieval Slavic texts. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2021-09-30T11:32:58', '2022-02-10T11:25:04');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(188898, 'Lithuanian Etymological Dictionary (en)', 'Lithuanian Etymological Dictionary (sv)', 'Lithuanian Etymological Dictionary offers a detailed discussion of the etymologies of the standard Lithuanian lexicon and of many words from the older Lithuanian literature and from Lithuanian dialects. (en)', 'Lithuanian Etymological Dictionary offers a detailed discussion of the etymologies of the standard Lithuanian lexicon and of many words from the older Lithuanian literature and from Lithuanian dialects. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2021-09-30T11:35:25', '2022-02-10T11:25:51');
INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(188900, 'zbMATH Open (en)', 'zbMATH Open (sv)', 'zbMATH Open is a comprehensive abstracting and reviewing service in pure and applied mathematics, edited by the European Mathematical Society (EMS), the Heidelberg Academy of Sciences and Humanities, and FIZ Karlsruhe. It provides access to bibliographic data, reviews and abstracts from all areas of pure mathematics as well as applications, in particular to natural sciences, computer science, economics and engineering. It also covers history and philosophy of mathematics and university education. (en)', 'zbMATH Open is a comprehensive abstracting and reviewing service in pure and applied mathematics, edited by the European Mathematical Society (EMS), the Heidelberg Academy of Sciences and Humanities, and FIZ Karlsruhe. It provides access to bibliographic data, reviews and abstracts from all areas of pure mathematics as well as applications, in particular to natural sciences, computer science, economics and engineering. It also covers history and philosophy of mathematics and university education. (sv)', FALSE, '', '', FALSE, TRUE, 'freely_available', FALSE, '2021-10-04T16:09:33', '2021-10-04T16:09:33');
