INSERT INTO databases(id, title_en, title_sv, description_en, description_sv, is_popular, malfunction_message_en, malfunction_message_sv, malfunction_message_active, public_access, access_information_code, direct_link_is_hidden, inserted_at, updated_at) VALUES(189066, 'Codices Vossiani Latini Online  (en)', 'Codices Vossiani Latini Online  (sv)', 'The Codices Vossiani Latini Online publishes all 363 codices which form the world-famous Latin part of Vossius’ manuscript collection held at Leiden University Library. The Codices Vossiani Latini count a large number of early medieval manuscripts, including major sources of many classic texts. The 363 codices in all comprise 40,278 openings, resulting in 84,266 images, including covers and flyleaves. (en)', 'The Codices Vossiani Latini Online publishes all 363 codices which form the world-famous Latin part of Vossius’ manuscript collection held at Leiden University Library. The Codices Vossiani Latini count a large number of early medieval manuscripts, including major sources of many classic texts. The 363 codices in all comprise 40,278 openings, resulting in 84,266 images, including covers and flyleaves. (sv)', FALSE, '', '', FALSE, FALSE, 'available_to_the_university_of_gothenburg_available_to_anyone_using_the_libraries_computers', FALSE, '2022-01-21T10:43:51', '2022-02-10T11:10:40');
